/*iphone */


$font-family: "IBM Plex Sans", sans-serif;
$colors: (
        "primary": #5867dd,
        "danger": #ff4651,
        "success": #1dc9b7,
        "warning": #ffb822,
        "info": #2786fb
);

*, *::after, *::before {
  box-sizing: border-box;
}

body {
  min-height: 100vh;
  overflow-x: hidden;
  font-family: $font-family;
  color:#495057;
}
button:focus,
button:visited,
button:active {
  outline: none;
}

.page {
  min-height: 100vh;
  display: flex;
  align-items: center;
  justify-content: center;
  background-image: radial-gradient( circle farthest-corner at 18.7% 37.8%,  rgba(250,250,250,1) 0%, rgba(225,234,238,1) 90% );

  &__content {
    position: relative;
  }

  &__footer {
    position: fixed;
    bottom: 0;
    right: 0;
    padding: 15px 25px;
    z-index: 201;
    background-color: rgba(#B79B6C, .3);
    color: #B79B6C;
    cursor: pointer;
    transition: .3s ease all;
    will-change: background-color;

    &:hover {
      background-color: rgba(#B79B6C, .5);
    }
  }

}

.phone {
  width: 290px;
  //width: 100vw;
  height: 100vh;
  max-height: 587px;
  background-color: black;
  border-radius: 45px;
  background-color: #0b3b8b;
  padding: 5px;
  position: relative;
  z-index: 2;
  transform-origin: center center;
  box-shadow: 0 35px 60px -24px rgba(black, 0.4);

  &::before,
  &::after{
    content: '';
    height: 4px;
    width: 100%;
    background-color: rgba(black, .25);
    position: absolute;
    left: 0;
  }

  &::before{
    top: 50px;
  }

  &::after {
    bottom: 50px;
  }


  &__body {
    border-radius: 42px;
    width: 100%;
    height: 100%;
    background-color: #061d45;
    position: relative;
    padding: 9px;
    z-index: 2;
  }

  &__notch {
    position: absolute;
    top: 8px;
    left: 25%;
    height: 22px;
    width: 50%;
    background-color: #061d45;
    border-radius: 0 0 17px 17px;
    z-index: 1;
  }

  &__speaker {
    position: absolute;
    height: 3px;
    width: 35px;
    left: 50%;
    top: 6px;
    transform: translateX(-50%);
    background-color: #04132d;

    &::before {
      content: '';
      width: 100%;
      height: 1px;
      position: absolute;
      top: 1px;
      border-top: 1px dashed #020409
    }

    &::after {
      content: '';
      width: 112%;
      height: 4px;
      left: -6%;
      border-radius: 5px;
      position: absolute;
      background-color: rgba(white, .03);
    }
  }

  &__camera {
    position: absolute;
    height: 10px;
    width: 10px;
    border-radius: 10px;
    right: 22%;
    top: 3px;
    background-color: #04132d;
    background-image: linear-gradient(to bottom, #020409 0%, #071e46 100%);

    &::after {
      content: '';
      width: 6px;
      height: 6px;
      border-radius: 10px;
      top: 2px;
      left: 2px;
      position: absolute;
      background-color: #083274;
      background-image: linear-gradient(to bottom, #083274 0%, #04132d 100%);
    }
  }

  &__view {
    width: 100%;
    height: 100%;
    background-color: white;
    border-radius: 36px;
    position: relative;
    overflow: hidden;
    box-shadow: 0 15px 30px -5px rgba(black, 0.4);

    &-inner {
      height: 100%;
      overflow: auto;
      display: flex;
      flex-direction: column;
      align-items: center;
      justify-content: center;
    }
  }

  &__btn {
    position: absolute;
    width: calc(100% + 8px);
    height: 100%;
    top: 0;
    left: -4px;
    z-index: -1;

    &--volume {
      display: flex;
      flex-direction: column;
      height: 92px;
      width: 4px;
      justify-content: space-between;
      position: absolute;
      left: 0;
      top: 115px;

      &-up,
      &-down {
        border: 0;
        padding: 0;
        width: 4px;
        height: 40px;
        background-color: #0b3b8b;
        border-radius: 2px 0 0 2px;
      }

    }

    &--mute {
      border: 0;
      padding: 0;
      width: 3px;
      height: 20px;
      background-color: #0b3b8b;
      border-radius: 1px 0 0 1px;
      position: absolute;
      top: 77px;
      left: 1px;

    }

    &--power {
      position: absolute;
      width: 4px;
      height: 60px;
      padding: 0;
      border: 0;
      background-color: #0b3b8b;
      border-radius: 0 2px 2px 0;
      top: 125px;
      right: 0;
    }
  }
}

.content {
  text-align: center;
  font-weight: 500;
  line-height: 1.4;
  padding: 0 30px;
}


.dot {
  width: 6px;
  height: 6px;
  border-radius: 6px;
  margin: 0 7px;
  background-color: rgba(#B79B6C, .25);

}
.dots {
  width: 100px;
  height: 100px;
  display: flex;
  flex-wrap: wrap;
  align-content: space-between;
  justify-content: space-between;
  position: absolute;
  top: -30px;
  right: -35px;
  opacity: 0;
  animation: dotsAnime .5s ease .5s forwards;
}

.waves {
  position: absolute;
  bottom: -25px;
  left: -35px;
  opacity: 0;
  animation: waveAnime .5s ease .6s forwards;
}
.st0{
  display:none;
  fill:#5528C6;
  opacity: 0.2
}
.st1{
  fill:none;
  stroke:#5528C6;
  stroke-width:2;
  stroke-linecap:round;
  stroke-linejoin:round;
  stroke-miterlimit:10;
  opacity: 0.2
}
.st3{
  display:inline;
  fill:none;
  stroke:#5528C6;
  stroke-miterlimit:10;
  opacity: 0.2}
.st4{
  display:inline;
  opacity: 0.2
}
.st5{
  fill:none;
  stroke:#5528C6;
  stroke-miterlimit:10;
  opacity: 0.2
}

.circle,
.square {
  position: fixed;
  top: 0;
  left: 0;
  z-index: 100;
  opacity: 0;
  animation: fadeIn .3s ease forwards 1s 1;
  pointer-events: none;
  wil-change: transform, opacity;

  @each $key, $value in $colors {
    &.bg-#{$key} {
      border: 20px solid $value;
    }
  }

  &.blurred {
    filter: blur(8px);
    z-index: 200;
  }
}

.square {
  transform: rotate(30deg);
}


.circle {
  border-radius: 50%;
}

.modal {
  position: fixed;
  top: 0;
  bottom: 0;
  right: 0;
  width: 100%;
  max-width: 320px;
  background-color: white;
  z-index: 205;
  display: flex;
  flex-direction: column;
  box-shadow: -8px 15px 50px rgba(black, .06);
  transform: translateX(100%);
  transition: .6s ease all;
  will-change: transform;


  &.active {
    transform: translateX(0);
  }

  &__header {
    display: flex;
    align-items: center;
    justify-content: space-between;
    background-color: rgba(#B79B6C, .1);
    padding: 16px 12px 16px 24px;
  }

  &__title {
    font-weight: 400;
    color: #B79B6C;
    font-size: 18px;
    margin: 0;

  }

  &__close {
    background-color: transparent;
    border: none;
    width: 32px;
    height: 32px;
    position: relative;
    border-radius: 10px;
    cursor: pointer;
    transition: .3s ease all;
    will-change: background-color;

    &:hover {
      background-color: rgba(#B79B6C, .3);
    }

    &::after {
      content: '';
      position: absolute;
      width: 12px;
      height: 12px;
      transform: rotate(45deg);
      border-right: 1px solid rgba(black, .5);
      border-top: 1px solid rgba(black, .5);;
      top: 10px;
      left: 6px;
    }
  }

  &__content {
    flex: 1;
    padding: 30px;
    line-height: 1.7;
    color: #95989c;

    .fa-laugh-wink {
      color: map-get($colors, 'warning');
    }
  }
}



@keyframes fadeIn {
  from {
    opacity: 0;
  }
  to {
    opacity: 1;
  }
}


@keyframes phoneAnime {
  0% {
    transform: rotate(45deg);
    opacity: 0;
  }

}

@keyframes dotsAnime {
  from {
    opacity: 0;
    transform: translate(-50%, 50%) scale(.4);
  }
  to {
    opacity: 1;
    transform: translate(0, 0) scale(1);
  }
}

@keyframes waveAnime {
  from {
    opacity: 0;
    transform: translateX(50%) scale(.4);
  }
  to {
    opacity: 1;
    transform: translateX(0) scale(1);
  }
}
